#!/bin/bash

# MCF Connector Order Routing Engine - Development Environment Setup
# Generated by Planner Agent

set -e

echo "========================================"
echo "MCF Connector Development Environment"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m' # No Color

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# SETUP PROJECT DIRECTORY
# ============================================

PROJECT_DIR="claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/05-mcf-connector-integration/implementation"

echo ""
echo "Setting up project directory..."
echo "Project path: $PROJECT_DIR"
echo ""

# Check if project directory exists
if [ ! -d "$PROJECT_DIR" ]; then
    echo -e "${YELLOW}Project directory does not exist yet.${NC}"
    echo "It will be created during Phase 1 (Project Setup)."
    echo ""
    echo "Once Phase 1 is complete, run this script again to start the dev environment."
    exit 0
fi

# ============================================
# INSTALL DEPENDENCIES
# ============================================

echo ""
echo "Installing dependencies..."
cd "$PROJECT_DIR"

if [ ! -f "package.json" ]; then
    echo -e "${RED}package.json not found. Run Phase 1 (Project Setup) first.${NC}"
    exit 1
fi

npm install

# ============================================
# ENVIRONMENT SETUP
# ============================================

echo ""
echo "Checking environment configuration..."

if [ ! -f ".env" ]; then
    if [ -f ".env.example" ]; then
        echo -e "${YELLOW}.env file not found. Creating from .env.example...${NC}"
        cp .env.example .env
        echo -e "${YELLOW}Please edit .env with your API credentials before running the connector.${NC}"
    else
        echo -e "${RED}.env.example not found. Run Phase 1 (Project Setup) first.${NC}"
        exit 1
    fi
fi

# ============================================
# BUILD PROJECT
# ============================================

echo ""
echo "Building TypeScript project..."
npm run build

if [ $? -eq 0 ]; then
    echo -e "${GREEN}Build successful!${NC}"
else
    echo -e "${RED}Build failed. Fix TypeScript errors before continuing.${NC}"
    exit 1
fi

# ============================================
# RUN TESTS (if available)
# ============================================

echo ""
echo "Running tests..."
npm test

if [ $? -eq 0 ]; then
    echo -e "${GREEN}All tests passed!${NC}"
else
    echo -e "${YELLOW}Some tests failed. Review test output above.${NC}"
fi

# ============================================
# START DEVELOPMENT SERVER
# ============================================

echo ""
echo "Starting MCF Connector in development mode..."
echo ""
echo -e "${GREEN}Press Ctrl+C to stop${NC}"
echo ""

npm run dev

# ============================================
# SUMMARY
# ============================================

echo ""
echo "========================================"
echo "MCF Connector Ready!"
echo "========================================"
echo ""
echo "Service: MCF Connector"
echo "Status: Running"
echo ""
echo "To test the connector:"
echo "  1. Ensure .env has valid TikTok Shop and Amazon MCF credentials"
echo "  2. Create a test order in TikTok Shop sandbox"
echo "  3. Monitor logs to verify order detection and routing"
echo ""
echo "Environment variables required:"
echo "  - TIKTOK_APP_KEY"
echo "  - TIKTOK_APP_SECRET"
echo "  - TIKTOK_ACCESS_TOKEN"
echo "  - TIKTOK_SHOP_ID"
echo "  - AMAZON_CLIENT_ID"
echo "  - AMAZON_CLIENT_SECRET"
echo "  - AMAZON_REFRESH_TOKEN"
echo "  - AMAZON_SELLER_ID"
echo "  - AMAZON_MARKETPLACE_ID"
echo ""
