     1â†’"use client";
     2â†’
     3â†’import { useWorkspace } from "@/contexts/WorkspaceContext";
     4â†’import { useState } from "react";
     5â†’import { Check, ChevronsUpDown, Plus } from "lucide-react";
     6â†’import { cn } from "@/lib/utils";
     7â†’
     8â†’/**
     9â†’ * WorkspaceSelector component allows users to view and switch between workspaces
    10â†’ *
    11â†’ * Features:
    12â†’ * - Displays current workspace with avatar
    13â†’ * - Dropdown list of all available workspaces
    14â†’ * - Visual indicator for active workspace
    15â†’ * - Option to create new workspace
    16â†’ * - Loading states during workspace switch
    17â†’ *
    18â†’ * Uses Clerk Organizations for multi-tenant workspace management.
    19â†’ *
    20â†’ * @example
    21â†’ * ```tsx
    22â†’ * <WorkspaceSelector />
    23â†’ * ```
    24â†’ */
    25â†’export function WorkspaceSelector() {
    26â†’  const {
    27â†’    currentWorkspace,
    28â†’    workspaces,
    29â†’    switchWorkspace,
    30â†’    isLoading,
    31â†’    hasWorkspaces,
    32â†’  } = useWorkspace();
    33â†’
    34â†’  const [isOpen, setIsOpen] = useState(false);
    35â†’  const [isSwitching, setIsSwitching] = useState(false);
    36â†’
    37â†’  const handleSwitchWorkspace = async (workspaceId: string) => {
    38â†’    if (workspaceId === currentWorkspace?.id) {
    39â†’      setIsOpen(false);
    40â†’      return;
    41â†’    }
    42â†’
    43â†’    try {
    44â†’      setIsSwitching(true);
    45â†’      await switchWorkspace(workspaceId);
    46â†’      setIsOpen(false);
    47â†’    } catch (error) {
    48â†’      // Error handling - in production, this should show a toast notification
    49â†’      // eslint-disable-next-line no-console
    50â†’      console.error("Failed to switch workspace:", error);
    51â†’    } finally {
    52â†’      setIsSwitching(false);
    53â†’    }
    54â†’  };
    55â†’
    56â†’  // Loading state
    57â†’  if (isLoading) {
    58â†’    return (
    59â†’      <div className="flex items-center gap-2 rounded-md border border-slate-200 bg-white px-3 py-2">
    60â†’        <div className="h-8 w-8 animate-pulse rounded-md bg-slate-200" />
    61â†’        <div className="flex-1">
    62â†’          <div className="h-4 w-24 animate-pulse rounded bg-slate-200" />
    63â†’        </div>
    64â†’      </div>
    65â†’    );
    66â†’  }
    67â†’
    68â†’  // No workspaces state
    69â†’  if (!hasWorkspaces || !currentWorkspace) {
    70â†’    return (
    71â†’      <div className="flex items-center gap-2 rounded-md border border-slate-200 bg-white px-3 py-2">
    72â†’        <div className="flex h-8 w-8 items-center justify-center rounded-md bg-slate-100">
    73â†’          <Plus className="h-4 w-4 text-slate-600" />
    74â†’        </div>
    75â†’        <div className="flex-1">
    76â†’          <p className="text-sm font-medium text-slate-900">No Workspace</p>
    77â†’          <p className="text-xs text-slate-500">Create or join a workspace</p>
    78â†’        </div>
    79â†’      </div>
    80â†’    );
    81â†’  }
    82â†’
    83â†’  return (
    84â†’    <div className="relative">
    85â†’      {/* Workspace selector button */}
    86â†’      <button
    87â†’        onClick={() => setIsOpen(!isOpen)}
    88â†’        disabled={isSwitching}
    89â†’        className={cn(
    90â†’          "flex w-full items-center gap-2 rounded-md border border-slate-200 bg-white px-3 py-2 text-left transition-colors hover:bg-slate-50",
    91â†’          isSwitching && "cursor-not-allowed opacity-50"
    92â†’        )}
    93â†’        aria-haspopup="listbox"
    94â†’        aria-expanded={isOpen}
    95â†’      >
    96â†’        {/* Workspace avatar */}
    97â†’        <div className="relative h-8 w-8 flex-shrink-0 overflow-hidden rounded-md bg-slate-100">
    98â†’          {currentWorkspace.imageUrl ? (
    99â†’            <img
   100â†’              src={currentWorkspace.imageUrl}
   101â†’              alt={currentWorkspace.name}
   102â†’              className="h-full w-full object-cover"
   103â†’            />
   104â†’          ) : (
   105â†’            <div className="flex h-full w-full items-center justify-center bg-gradient-to-br from-blue-500 to-purple-600 text-sm font-semibold text-white">
   106â†’              {currentWorkspace.name.substring(0, 2).toUpperCase()}
   107â†’            </div>
   108â†’          )}
   109â†’        </div>
   110â†’
   111â†’        {/* Workspace name */}
   112â†’        <div className="min-w-0 flex-1">
   113â†’          <p className="truncate text-sm font-medium text-slate-900">
   114â†’            {currentWorkspace.name}
   115â†’          </p>
   116â†’          <p className="text-xs text-slate-500">
   117â†’            {currentWorkspace.membersCount}{" "}
   118â†’            {currentWorkspace.membersCount === 1 ? "member" : "members"}
   119â†’          </p>
   120â†’        </div>
   121â†’
   122â†’        {/* Dropdown icon */}
   123â†’        <ChevronsUpDown className="h-4 w-4 flex-shrink-0 text-slate-400" />
   124â†’      </button>
   125â†’
   126â†’      {/* Dropdown menu */}
   127â†’      {isOpen && (
   128â†’        <>
   129â†’          {/* Backdrop to close dropdown */}
   130â†’          <div
   131â†’            className="fixed inset-0 z-10"
   132â†’            onClick={() => setIsOpen(false)}
   133â†’          />
   134â†’
   135â†’          {/* Workspace list */}
   136â†’          <div className="absolute left-0 right-0 top-full z-20 mt-2 max-h-80 overflow-auto rounded-md border border-slate-200 bg-white shadow-lg">
   137â†’            <div className="p-1">
   138â†’              {/* Existing workspaces */}
   139â†’              <div className="mb-1 px-2 py-1.5">
   140â†’                <p className="text-xs font-semibold uppercase text-slate-500">
   141â†’                  Your Workspaces
   142â†’                </p>
   143â†’              </div>
   144â†’
   145â†’              {workspaces.map((workspace) => {
   146â†’                const isActive = workspace.id === currentWorkspace.id;
   147â†’
   148â†’                return (
   149â†’                  <button
   150â†’                    key={workspace.id}
   151â†’                    onClick={() => handleSwitchWorkspace(workspace.id)}
   152â†’                    disabled={isSwitching}
   153â†’                    className={cn(
   154â†’                      "flex w-full items-center gap-2 rounded-md px-2 py-2 text-left transition-colors",
   155â†’                      isActive
   156â†’                        ? "bg-slate-100"
   157â†’                        : "hover:bg-slate-50 active:bg-slate-100",
   158â†’                      isSwitching && "cursor-not-allowed opacity-50"
   159â†’                    )}
   160â†’                  >
   161â†’                    {/* Workspace avatar */}
   162â†’                    <div className="relative h-8 w-8 flex-shrink-0 overflow-hidden rounded-md bg-slate-100">
   163â†’                      {workspace.imageUrl ? (
   164â†’                        <img
   165â†’                          src={workspace.imageUrl}
   166â†’                          alt={workspace.name}
   167â†’                          className="h-full w-full object-cover"
   168â†’                        />
   169â†’                      ) : (
   170â†’                        <div className="flex h-full w-full items-center justify-center bg-gradient-to-br from-blue-500 to-purple-600 text-xs font-semibold text-white">
   171â†’                          {workspace.name.substring(0, 2).toUpperCase()}
   172â†’                        </div>
   173â†’                      )}
   174â†’                    </div>
   175â†’
   176â†’                    {/* Workspace info */}
   177â†’                    <div className="min-w-0 flex-1">
   178â†’                      <p className="truncate text-sm font-medium text-slate-900">
   179â†’                        {workspace.name}
   180â†’                      </p>
   181â†’                      <p className="text-xs text-slate-500">
   182â†’                        {workspace.membersCount}{" "}
   183â†’                        {workspace.membersCount === 1 ? "member" : "members"}
   184â†’                      </p>
   185â†’                    </div>
   186â†’
   187â†’                    {/* Active indicator */}
   188â†’                    {isActive && (
   189â†’                      <Check className="h-4 w-4 flex-shrink-0 text-blue-600" />
   190â†’                    )}
   191â†’                  </button>
   192â†’                );
   193â†’              })}
   194â†’
   195â†’              {/* Create new workspace option */}
   196â†’              <div className="mt-1 border-t border-slate-200 pt-1">
   197â†’                <button
   198â†’                  onClick={() => {
   199â†’                    // In production, this would trigger Clerk's organization creation modal
   200â†’                    // For now, we'll just close the dropdown
   201â†’                    setIsOpen(false);
   202â†’                  }}
   203â†’                  className="flex w-full items-center gap-2 rounded-md px-2 py-2 text-left transition-colors hover:bg-slate-50"
   204â†’                >
   205â†’                  <div className="flex h-8 w-8 flex-shrink-0 items-center justify-center rounded-md bg-slate-100">
   206â†’                    <Plus className="h-4 w-4 text-slate-600" />
   207â†’                  </div>
   208â†’                  <div>
   209â†’                    <p className="text-sm font-medium text-slate-900">
   210â†’                      Create Workspace
   211â†’                    </p>
   212â†’                    <p className="text-xs text-slate-500">
   213â†’                      Start a new workspace
   214â†’                    </p>
   215â†’                  </div>
   216â†’                </button>
   217â†’              </div>
   218â†’            </div>
   219â†’          </div>
   220â†’        </>
   221â†’      )}
   222â†’    </div>
   223â†’  );
   224â†’}
   225â†’

