     6â†’/**
     7â†’ * ConfigField component provides a reusable form field for configuration forms
     8â†’ *
     9â†’ * Features:
    10â†’ * - Supports text, textarea, select, and number input types
    11â†’ * - Label with optional/required indicator
    12â†’ * - Helper text for field descriptions
    13â†’ * - Error state with error message display
    14â†’ * - Disabled and loading states
    15â†’ * - Consistent styling with Tailwind CSS
    16â†’ * - Responsive design
    17â†’ *
    18â†’ * @example
    19â†’ * ```tsx
    20â†’ * <ConfigField
    21â†’ *   id="api-key"
    22â†’ *   label="API Key"
    23â†’ *   type="text"
    24â†’ *   value={apiKey}
    25â†’ *   onChange={(e) => setApiKey(e.target.value)}
    26â†’ *   placeholder="Enter your API key"
    27â†’ *   required
    28â†’ *   helperText="Your API key from the provider's dashboard"
    29â†’ *   error={errors.apiKey}
    30â†’ * />
    31â†’ * ```
    32â†’ */
    33â†’
    34â†’export interface ConfigFieldProps {
    35â†’  /** Unique identifier for the field */
    36â†’  id: string;
    37â†’  /** Label text displayed above the field */
    38â†’  label: string;
    39â†’  /** Input type - text, textarea, select, or number */
    40â†’  type?: "text" | "textarea" | "select" | "number";
    41â†’  /** Current field value */
    42â†’  value: string | number;
    43â†’  /** Change handler */
    44â†’  onChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => void;
    45â†’  /** Placeholder text */
    46â†’  placeholder?: string;
    47â†’  /** Whether the field is required */
    48â†’  required?: boolean;
    49â†’  /** Whether the field is disabled */
    50â†’  disabled?: boolean;
    51â†’  /** Helper text displayed below the field */
    52â†’  helperText?: string;
    53â†’  /** Error message - if present, field shows error state */
    54â†’  error?: string;
    55â†’  /** Options for select type (array of {value, label}) */
    56â†’  options?: Array<{ value: string; label: string }>;
    57â†’  /** Optional custom className for wrapper */
    58â†’  className?: string;
    59â†’}
    60â†’
    61â†’export function ConfigField({
    62â†’  id,
    63â†’  label,
    64â†’  type = "text",
    65â†’  value,
    66â†’  onChange,
    67â†’  placeholder,
    68â†’  required = false,
    69â†’  disabled = false,
    70â†’  helperText,
    71â†’  error,
    72â†’  options,
    73â†’  className,
    74â†’}: ConfigFieldProps) {
    75â†’  const hasError = Boolean(error);
    76â†’
    77â†’  // Base input classes
    78â†’  const inputClasses = cn(
    79â†’    "w-full rounded-lg border px-3 py-2 text-sm transition-colors",
    80â†’    "placeholder:text-slate-400",
    81â†’    "focus:outline-none focus:ring-2",
    82â†’    hasError
    83â†’      ? "border-red-300 bg-red-50 text-red-900 focus:border-red-500 focus:ring-red-200"
    84â†’      : "border-slate-300 bg-white text-slate-900 focus:border-blue-500 focus:ring-blue-200",
    85â†’    disabled && "cursor-not-allowed bg-slate-50 text-slate-500"
    86â†’  );
    87â†’
    88â†’  return (
    89â†’    <div className={cn("space-y-1.5", className)}>
    90â†’      {/* Label */}
    91â†’      <label
    92â†’        htmlFor={id}
    93â†’        className="flex items-center gap-1 text-sm font-medium text-slate-700"
    94â†’      >
    95â†’        {label}
    96â†’        {required && <span className="text-red-500">*</span>}
    97â†’      </label>
    98â†’
    99â†’      {/* Input Field */}
   100â†’      {type === "textarea" ? (
   101â†’        <textarea
   102â†’          id={id}
   103â†’          value={value}
   104â†’          onChange={onChange}
   105â†’          placeholder={placeholder}
   106â†’          required={required}
   107â†’          disabled={disabled}
   108â†’          rows={4}
   109â†’          className={inputClasses}
   110â†’        />
   111â†’      ) : type === "select" ? (
   112â†’        <select
   113â†’          id={id}
   114â†’          value={value}
   115â†’          onChange={onChange}
   116â†’          required={required}
   117â†’          disabled={disabled}
   118â†’          className={inputClasses}
   119â†’        >
   120â†’          <option value="">Select an option</option>
   121â†’          {options?.map((option) => (
   122â†’            <option key={option.value} value={option.value}>
   123â†’              {option.label}
   124â†’            </option>
   125â†’          ))}
   126â†’        </select>
   127â†’      ) : (
   128â†’        <input
   129â†’          id={id}
   130â†’          type={type}
   131â†’          value={value}
   132â†’          onChange={onChange}
   133â†’          placeholder={placeholder}
   134â†’          required={required}
   135â†’          disabled={disabled}
   136â†’          className={inputClasses}
   137â†’        />
   138â†’      )}
   139â†’
   140â†’      {/* Helper Text / Error Message */}
   141â†’      {hasError ? (
   142â†’        <div className="flex items-center gap-1.5 text-sm text-red-600">
   143â†’          <AlertCircle className="h-4 w-4" />
   144â†’          <span>{error}</span>
   145â†’        </div>
   146â†’      ) : helperText ? (
   147â†’        <p className="text-sm text-slate-500">{helperText}</p>
   148â†’      ) : null}
   149â†’    </div>
   150â†’  );
   151â†’}
