    21â†’ * - Fetches real-time data from /api/metrics endpoint
    22â†’ * - Responsive grid layout (1 col mobile, 2 col tablet, 3 col desktop)
    23â†’ * - TikTok views, email subscribers, blog traffic, revenue, conversion rate, total reach
    24â†’ * - Loading state support for all metrics
    25â†’ * - Color-coded trend indicators
    26â†’ * - Optional polling for real-time updates
    27â†’ *
    28â†’ * @example
    29â†’ * ```tsx
    30â†’ * <KPIOverview pollingInterval={60000} />
    31â†’ * ```
    32â†’ */
    33â†’
    34â†’export interface KPIMetric {
    35â†’  /** Title of the metric */
    36â†’  title: string;
    37â†’  /** Current value to display */
    38â†’  value: string;
    39â†’  /** Percentage change (positive or negative) */
    40â†’  change: number;
    41â†’  /** Icon component from lucide-react */
    42â†’  icon: LucideIcon;
    43â†’  /** Description or subtitle */
    44â†’  description: string;
    45â†’}
    46â†’
    47â†’export interface KPIOverviewProps {
    48â†’  /** Optional custom className for grid wrapper */
    49â†’  className?: string;
    50â†’  /** Optional polling interval in milliseconds (default: disabled) */
    51â†’  pollingInterval?: number;
    52â†’}
    53â†’
    54â†’/**
    55â†’ * Format number to display-friendly string
    56â†’ */
    57â†’function formatNumber(num: number): string {
    58â†’  if (num >= 1000000) {
    59â†’    return (num / 1000000).toFixed(1) + "M";
    60â†’  }
    61â†’  if (num >= 1000) {
    62â†’    return (num / 1000).toFixed(1) + "K";
    63â†’  }
    64â†’  return num.toLocaleString();
    65â†’}
    66â†’
    67â†’/**
    68â†’ * Format currency to display-friendly string
    69â†’ */
    70â†’function formatCurrency(num: number): string {
    71â†’  return "$" + num.toLocaleString();
    72â†’}
    73â†’
    74â†’export function KPIOverview({
    75â†’  className = "",
    76â†’  pollingInterval,
    77â†’}: KPIOverviewProps) {
    78â†’  // Fetch metrics from API with optional polling
    79â†’  const { metrics, isLoading, error } = useMetrics({ pollingInterval });
    80â†’
    81â†’  // Transform API metrics to KPI format
    82â†’  const kpiMetrics = useMemo<KPIMetric[]>(() => {
    83â†’    if (!metrics) {
    84â†’      return [];
    85â†’    }
    86â†’
    87â†’    const tiktok = metrics.services.find((s) => s.service === "tiktok");
    88â†’    const blog = metrics.services.find((s) => s.service === "blog");
    89â†’    const email = metrics.services.find((s) => s.service === "email");
    90â†’
    91â†’    return [
    92â†’      {
    93â†’        title: "TikTok Views",
    94â†’        value: formatNumber(tiktok?.metrics.views?.value || 0),
    95â†’        change: tiktok?.metrics.views?.change || 0,
    96â†’        icon: Eye,
    97â†’        description: "Total video views",
    98â†’      },
    99â†’      {
   100â†’        title: "Email Subscribers",
