   109â†’        change: blog?.metrics.pageViews?.change || 0,
   110â†’        icon: FileText,
   111â†’        description: "Monthly page views",
   112â†’      },
   113â†’      {
   114â†’        title: "Revenue",
   115â†’        value: formatCurrency(metrics.summary.totalRevenue?.value || 0),
   116â†’        change: metrics.summary.totalRevenue?.change || 0,
   117â†’        icon: DollarSign,
   118â†’        description: "Last 30 days",
   119â†’      },
   120â†’      {
   121â†’        title: "Conversion Rate",
   122â†’        value: (metrics.summary.conversionRate?.value || 0).toFixed(2) + "%",
   123â†’        change: metrics.summary.conversionRate?.change || 0,
   124â†’        icon: TrendingUp,
   125â†’        description: "Visitor to customer",
   126â†’      },
   127â†’      {
   128â†’        title: "Total Reach",
   129â†’        value: formatNumber(metrics.summary.totalReach?.value || 0),
   130â†’        change: metrics.summary.totalReach?.change || 0,
   131â†’        icon: Users,
   132â†’        description: "Across all channels",
   133â†’      },
   134â†’    ];
   135â†’  }, [metrics]);
   136â†’
   137â†’  // Show error state if metrics fetch fails
   138â†’  if (error) {
   139â†’    return (
   140â†’      <div className={className}>
   141â†’        <h2 className="mb-4 text-lg font-semibold text-slate-900">
   142â†’          Key Performance Indicators
   143â†’        </h2>
   144â†’        <div className="rounded-lg border border-red-200 bg-red-50 p-6">
   145â†’          <p className="text-sm text-red-600">
   146â†’            Failed to load metrics: {error.message}
   147â†’          </p>
   148â†’        </div>
   149â†’      </div>
   150â†’    );
   151â†’  }
   152â†’
   153â†’  return (
   154â†’    <div className={className}>
   155â†’      <h2 className="mb-4 text-lg font-semibold text-slate-900">
   156â†’        Key Performance Indicators
   157â†’      </h2>
   158â†’      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
   159â†’        {kpiMetrics.map((metric, index) => (
   160â†’          <MetricCard
   161â†’            key={index}
   162â†’            title={metric.title}
   163â†’            value={metric.value}
   164â†’            change={metric.change}
   165â†’            icon={metric.icon}
   166â†’            description={metric.description}
   167â†’            isLoading={isLoading}
   168â†’          />
   169â†’        ))}
