#!/bin/bash

# TikTok Content Scheduling & Auto-Publishing - Development Environment Setup
# Generated by Planner Agent

set -e

echo "========================================"
echo "Starting Development Environment"
echo "TikTok Content Scheduling & Auto-Publishing"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# PROJECT ROOT
# ============================================
PROJECT_ROOT="./claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents"

# ============================================
# SETUP VIRTUAL ENVIRONMENT
# ============================================
echo ""
echo -e "${YELLOW}Setting up virtual environment...${NC}"
cd "$PROJECT_ROOT"

if [ ! -d "venv" ]; then
    echo "Creating virtual environment..."
    python3 -m venv venv
fi

echo "Activating virtual environment..."
source venv/bin/activate

echo "Installing dependencies..."
pip install -q --upgrade pip
pip install -q -r requirements.txt

# ============================================
# DATABASE SETUP
# ============================================
echo ""
echo -e "${YELLOW}Setting up database...${NC}"
python database/init_db.py

# ============================================
# START SERVICES
# ============================================
echo ""
echo -e "${YELLOW}Starting services...${NC}"

# Start Backend API
echo "Starting Backend API..."
uvicorn api.main:app --host 0.0.0.0 --port 8000 --reload > logs/backend.log 2>&1 &
BACKEND_PID=$!
wait_for_service 8000 "Backend API"

# Start Scheduler Service
echo "Starting Scheduler Service..."
python scheduler/scheduler_service.py > logs/scheduler.log 2>&1 &
SCHEDULER_PID=$!
sleep 2
echo -e "${GREEN}Scheduler Service started (PID: $SCHEDULER_PID)${NC}"

# ============================================
# SUMMARY
# ============================================
echo ""
echo "========================================"
echo "Environment Ready!"
echo "========================================"
echo ""
echo "Services:"
echo "  Backend API:  http://localhost:8000"
echo "  API Docs:     http://localhost:8000/api/docs"
echo "  Health Check: http://localhost:8000/health"
echo ""
echo "Background Services:"
echo "  Scheduler:    Running (PID: $SCHEDULER_PID)"
echo ""
echo "Logs:"
echo "  Backend:      $PROJECT_ROOT/logs/backend.log"
echo "  Scheduler:    $PROJECT_ROOT/logs/scheduler.log"
echo ""
echo "PIDs:"
echo "  Backend:      $BACKEND_PID"
echo "  Scheduler:    $SCHEDULER_PID"
echo ""
echo "To stop services:"
echo "  kill $BACKEND_PID $SCHEDULER_PID"
echo ""
echo "========================================"
