#!/bin/bash

# Auto-Build Environment Setup for Blog Platform Deployment
# Generated by Planner Agent

set -e

echo "========================================"
echo "Blog Platform Deployment - Environment Setup"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# CHECK DEPENDENCIES
# ============================================

echo ""
echo "Checking dependencies..."

# Check Node.js
if ! command -v node &> /dev/null; then
    echo -e "${RED}Node.js not found. Please install Node.js 18+ first.${NC}"
    exit 1
fi
NODE_VERSION=$(node --version)
echo -e "${GREEN}✓ Node.js $NODE_VERSION${NC}"

# Check npm
if ! command -v npm &> /dev/null; then
    echo -e "${RED}npm not found. Please install npm first.${NC}"
    exit 1
fi
NPM_VERSION=$(npm --version)
echo -e "${GREEN}✓ npm $NPM_VERSION${NC}"

# Check Python (for content-agents backend)
if ! command -v python3 &> /dev/null; then
    echo -e "${YELLOW}⚠ Python 3 not found. Content-agents backend will not be available.${NC}"
else
    PYTHON_VERSION=$(python3 --version)
    echo -e "${GREEN}✓ $PYTHON_VERSION${NC}"
fi

# ============================================
# SETUP BLOG PROJECT
# ============================================

BLOG_DIR="./claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/blog"

if [ ! -d "$BLOG_DIR" ]; then
    echo ""
    echo -e "${YELLOW}Blog directory not yet created. Run coder agent to implement subtasks.${NC}"
    echo ""
    echo "========================================"
    echo "Environment Check Complete"
    echo "========================================"
    echo ""
    echo "Next Steps:"
    echo "1. Start the coder agent: python auto-claude/run.py --spec 009"
    echo "2. The coder will create the blog project structure"
    echo "3. Once created, run this script again to start the dev server"
    exit 0
fi

echo ""
echo "Installing blog dependencies..."
cd "$BLOG_DIR"
npm install
echo -e "${GREEN}✓ Blog dependencies installed${NC}"

# ============================================
# START SERVICES
# ============================================

echo ""
echo "========================================"
echo "Starting Development Services"
echo "========================================"
echo ""

# Start blog development server
echo "Starting blog server..."
cd "$BLOG_DIR"
npm run dev &
BLOG_PID=$!
wait_for_service 3000 "Blog"

# Optional: Start content-agents backend if available
CONTENT_AGENTS_DIR="./claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents"
if [ -d "$CONTENT_AGENTS_DIR" ] && [ -f "$CONTENT_AGENTS_DIR/requirements.txt" ]; then
    echo ""
    echo "Starting content-agents backend (optional)..."
    cd "$CONTENT_AGENTS_DIR"

    # Check if virtual environment exists
    if [ ! -d "venv" ]; then
        echo "Creating Python virtual environment..."
        python3 -m venv venv
    fi

    # Activate virtual environment
    source venv/bin/activate

    # Install dependencies
    pip install -r requirements.txt > /dev/null 2>&1

    # Start API server
    python -m uvicorn api.main:app --reload --port 8000 &
    CONTENT_AGENTS_PID=$!
    wait_for_service 8000 "Content-agents API"
fi

# ============================================
# SUMMARY
# ============================================

echo ""
echo "========================================"
echo "Environment Ready!"
echo "========================================"
echo ""
echo "Services:"
echo "  Blog:          http://localhost:3000"
if [ ! -z "$CONTENT_AGENTS_PID" ]; then
    echo "  Content API:   http://localhost:8000"
fi
echo ""
echo "Useful URLs:"
echo "  Blog Index:    http://localhost:3000/blog"
echo "  API Docs:      http://localhost:8000/docs (if content-agents running)"
echo ""
echo "To stop services:"
echo "  Press Ctrl+C or run: kill $BLOG_PID"
if [ ! -z "$CONTENT_AGENTS_PID" ]; then
    echo "  Also stop content-agents: kill $CONTENT_AGENTS_PID"
fi
echo ""
echo "========================================"

# Keep script running
wait
