     1â†’/**
     2â†’ * CMS Integration Layer
     3â†’ * File-based content management using MDX files stored in content/posts/
     4â†’ * Provides functions to read, parse, and query blog posts
     5â†’ */
     6â†’
     7â†’import fs from 'fs';
     8â†’import path from 'path';
     9â†’import matter from 'gray-matter';
    10â†’import readingTime from 'reading-time';
    11â†’import type {
    12â†’  BlogPost,
    13â†’  BlogPostMeta,
    14â†’  BlogPostFrontmatter,
    15â†’  Category,
    16â†’  Tag,
    17â†’} from './types';
    18â†’
    19â†’const CONTENT_DIR = path.join(process.cwd(), 'content', 'posts');
    20â†’
    21â†’/**
    22â†’ * Ensure content directory exists
    23â†’ */
    24â†’function ensureContentDirectory(): void {
    25â†’  if (!fs.existsSync(CONTENT_DIR)) {
    26â†’    fs.mkdirSync(CONTENT_DIR, { recursive: true });
    27â†’  }
    28â†’}
    29â†’
    30â†’/**
    31â†’ * Get all MDX/MD files from content directory
    32â†’ */
    33â†’function getPostFiles(): string[] {
    34â†’  ensureContentDirectory();
    35â†’
    36â†’  if (!fs.existsSync(CONTENT_DIR)) {
    37â†’    return [];
    38â†’  }
    39â†’
    40â†’  const files = fs.readdirSync(CONTENT_DIR);
    41â†’  return files.filter(
    42â†’    (file) => file.endsWith('.mdx') || file.endsWith('.md')
    43â†’  );
    44â†’}
    45â†’
    46â†’/**
    47â†’ * Parse a blog post file and return structured data
    48â†’ */
    49â†’function parsePostFile(filename: string): BlogPost | null {
    50â†’  try {
    51â†’    const slug = filename.replace(/\.(mdx|md)$/, '');
    52â†’    const filePath = path.join(CONTENT_DIR, filename);
    53â†’    const fileContents = fs.readFileSync(filePath, 'utf8');
    54â†’
    55â†’    const { data, content } = matter(fileContents);
    56â†’    const frontmatter = data as BlogPostFrontmatter;
    57â†’
    58â†’    // Calculate reading time
    59â†’    const stats = readingTime(content);
    60â†’
    61â†’    // Default values
    62â†’    const post: BlogPost = {
    63â†’      slug,
    64â†’      title: frontmatter.title || 'Untitled',
    65â†’      description: frontmatter.description || '',
    66â†’      date: frontmatter.date || new Date().toISOString(),
    67â†’      category: frontmatter.category || 'uncategorized',
    68â†’      tags: frontmatter.tags || [],
    69â†’      author: frontmatter.author || 'InfinityCards Team',
    70â†’      image: frontmatter.image,
    71â†’      content,
    72â†’      readingTime: Math.ceil(stats.minutes),
    73â†’      published: frontmatter.published !== false, // Default to true
    74â†’    };
    75â†’
    76â†’    return post;
    77â†’  } catch (error) {
    78â†’    console.error(`Error parsing post file ${filename}:`, error);
    79â†’    return null;
    80â†’  }
    81â†’}
    82â†’
    83â†’/**
    84â†’ * Get all blog posts
    85â†’ * @param includeUnpublished - Whether to include unpublished posts (default: false)
    86â†’ * @returns Array of blog posts sorted by date (newest first)
    87â†’ */
    88â†’export function getAllPosts(includeUnpublished = false): BlogPost[] {
    89â†’  const files = getPostFiles();
    90â†’  const posts = files
    91â†’    .map(parsePostFile)
    92â†’    .filter((post): post is BlogPost => post !== null)
    93â†’    .filter((post) => includeUnpublished || post.published)
    94â†’    .sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());
    95â†’
    96â†’  return posts;
    97â†’}
    98â†’
    99â†’/**
   100â†’ * Get all blog post metadata (without content)
   101â†’ * @param includeUnpublished - Whether to include unpublished posts (default: false)
   102â†’ * @returns Array of blog post metadata sorted by date (newest first)
   103â†’ */
   104â†’export function getAllPostsMeta(includeUnpublished = false): BlogPostMeta[] {
   105â†’  const posts = getAllPosts(includeUnpublished);
   106â†’  return posts.map(({ content, ...meta }) => meta);
   107â†’}
   108â†’
   109â†’/**
   110â†’ * Get a single blog post by slug
   111â†’ * @param slug - The post slug
   112â†’ * @returns Blog post or null if not found
   113â†’ */
   114â†’export function getPostBySlug(slug: string): BlogPost | null {
   115â†’  const filename = `${slug}.mdx`;
   116â†’  const altFilename = `${slug}.md`;
   117â†’
   118â†’  if (fs.existsSync(path.join(CONTENT_DIR, filename))) {
   119â†’    return parsePostFile(filename);
   120â†’  } else if (fs.existsSync(path.join(CONTENT_DIR, altFilename))) {
   121â†’    return parsePostFile(altFilename);
   122â†’  }
   123â†’
   124â†’  return null;
   125â†’}
   126â†’
   127â†’/**
   128â†’ * Get all posts in a specific category
   129â†’ * @param categorySlug - The category slug
   130â†’ * @param includeUnpublished - Whether to include unpublished posts (default: false)
   131â†’ * @returns Array of blog posts in the category
   132â†’ */
   133â†’export function getPostsByCategory(
   134â†’  categorySlug: string,
   135â†’  includeUnpublished = false
   136â†’): BlogPost[] {
   137â†’  const posts = getAllPosts(includeUnpublished);
   138â†’  return posts.filter(
   139â†’    (post) => post.category.toLowerCase() === categorySlug.toLowerCase()
   140â†’  );
   141â†’}
   142â†’
   143â†’/**
   144â†’ * Get all posts with a specific tag
   145â†’ * @param tagSlug - The tag slug
   146â†’ * @param includeUnpublished - Whether to include unpublished posts (default: false)
   147â†’ * @returns Array of blog posts with the tag
   148â†’ */
   149â†’export function getPostsByTag(
   150â†’  tagSlug: string,
   151â†’  includeUnpublished = false
   152â†’): BlogPost[] {
   153â†’  const posts = getAllPosts(includeUnpublished);
   154â†’  return posts.filter((post) =>
   155â†’    post.tags.some((tag) => tag.toLowerCase() === tagSlug.toLowerCase())
   156â†’  );
   157â†’}
   158â†’
   159â†’/**
   160â†’ * Get all unique categories with post counts
   161â†’ * @returns Array of categories sorted by count (descending)
   162â†’ */
   163â†’export function getAllCategories(): Category[] {
   164â†’  const posts = getAllPosts();
   165â†’  const categoryMap = new Map<string, number>();
   166â†’
   167â†’  posts.forEach((post) => {
   168â†’    const category = post.category.toLowerCase();
   169â†’    categoryMap.set(category, (categoryMap.get(category) || 0) + 1);
   170â†’  });
   171â†’
   172â†’  const categories: Category[] = Array.from(categoryMap.entries()).map(
   173â†’    ([slug, count]) => ({
   174â†’      name: slug
   175â†’        .split('-')
   176â†’        .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
   177â†’        .join(' '),
   178â†’      slug,
   179â†’      description: '', // Can be enhanced with category descriptions later
   180â†’      count,
   181â†’    })
   182â†’  );
   183â†’
   184â†’  return categories.sort((a, b) => b.count - a.count);
   185â†’}
   186â†’
   187â†’/**
   188â†’ * Get all unique tags with post counts
   189â†’ * @returns Array of tags sorted by count (descending)
   190â†’ */
   191â†’export function getAllTags(): Tag[] {
   192â†’  const posts = getAllPosts();
   193â†’  const tagMap = new Map<string, number>();
   194â†’
   195â†’  posts.forEach((post) => {
   196â†’    post.tags.forEach((tag) => {
   197â†’      const tagSlug = tag.toLowerCase();
   198â†’      tagMap.set(tagSlug, (tagMap.get(tagSlug) || 0) + 1);
   199â†’    });
   200â†’  });
   201â†’
   202â†’  const tags: Tag[] = Array.from(tagMap.entries()).map(([slug, count]) => ({
   203â†’    name: slug
   204â†’      .split('-')
   205â†’      .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
   206â†’      .join(' '),
   207â†’    slug,
   208â†’    count,
   209â†’  }));
   210â†’
   211â†’  return tags.sort((a, b) => b.count - a.count);
   212â†’}
   213â†’
   214â†’/**
   215â†’ * Get related posts based on category and tags
   216â†’ * @param slug - The current post slug
   217â†’ * @param limit - Maximum number of related posts to return (default: 3)
   218â†’ * @returns Array of related blog posts
   219â†’ */
   220â†’export function getRelatedPosts(slug: string, limit = 3): BlogPost[] {
   221â†’  const currentPost = getPostBySlug(slug);
   222â†’  if (!currentPost) return [];
   223â†’
   224â†’  const allPosts = getAllPosts();
   225â†’
   226â†’  // Score posts based on shared category and tags
   227â†’  const scoredPosts = allPosts
   228â†’    .filter((post) => post.slug !== slug)
   229â†’    .map((post) => {
   230â†’      let score = 0;
   231â†’
   232â†’      // Same category: +3 points
   233â†’      if (post.category === currentPost.category) {
   234â†’        score += 3;
   235â†’      }
   236â†’
   237â†’      // Shared tags: +1 point per tag
   238â†’      const sharedTags = post.tags.filter((tag) =>
   239â†’        currentPost.tags.includes(tag)
   240â†’      );
   241â†’      score += sharedTags.length;
   242â†’
   243â†’      return { post, score };
   244â†’    })
   245â†’    .filter(({ score }) => score > 0)
   246â†’    .sort((a, b) => b.score - a.score);
   247â†’
   248â†’  return scoredPosts.slice(0, limit).map(({ post }) => post);
   249â†’}
   250â†’
   251â†’/**
   252â†’ * Get recent posts
   253â†’ * @param limit - Maximum number of posts to return (default: 5)
   254â†’ * @returns Array of recent blog posts
   255â†’ */
   256â†’export function getRecentPosts(limit = 5): BlogPost[] {
   257â†’  const posts = getAllPosts();
   258â†’  return posts.slice(0, limit);
   259â†’}
   260â†’
   261â†’/**
   262â†’ * Check if content directory exists and has posts
   263â†’ * @returns True if content directory exists and has at least one post
   264â†’ */
   265â†’export function hasContent(): boolean {
   266â†’  return getPostFiles().length > 0;
   267â†’}
   268â†’

