   146â†’ * @param includeUnpublished - Whether to include unpublished posts (default: false)
   147â†’ * @returns Array of blog posts with the tag
   148â†’ */
   149â†’export function getPostsByTag(
   150â†’  tagSlug: string,
   151â†’  includeUnpublished = false
   152â†’): BlogPost[] {
   153â†’  const posts = getAllPosts(includeUnpublished);
   154â†’  return posts.filter((post) =>
   155â†’    post.tags.some((tag) => tag.toLowerCase() === tagSlug.toLowerCase())
   156â†’  );
   157â†’}
   158â†’
   159â†’/**
   160â†’ * Get all unique categories with post counts
   161â†’ * @returns Array of categories sorted by count (descending)
   162â†’ */
   163â†’export function getAllCategories(): Category[] {
   164â†’  const posts = getAllPosts();
   165â†’  const categoryMap = new Map<string, number>();
   166â†’
   167â†’  posts.forEach((post) => {
   168â†’    const category = post.category.toLowerCase();
   169â†’    categoryMap.set(category, (categoryMap.get(category) || 0) + 1);
   170â†’  });
   171â†’
   172â†’  const categories: Category[] = Array.from(categoryMap.entries()).map(
   173â†’    ([slug, count]) => ({
   174â†’      name: slug
   175â†’        .split('-')
   176â†’        .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
   177â†’        .join(' '),
   178â†’      slug,
   179â†’      description: '', // Can be enhanced with category descriptions later
   180â†’      count,
   181â†’    })
   182â†’  );
   183â†’
   184â†’  return categories.sort((a, b) => b.count - a.count);
   185â†’}
   186â†’
   187â†’/**
   188â†’ * Get all unique tags with post counts
   189â†’ * @returns Array of tags sorted by count (descending)
   190â†’ */
   191â†’export function getAllTags(): Tag[] {
   192â†’  const posts = getAllPosts();
   193â†’  const tagMap = new Map<string, number>();
   194â†’
   195â†’  posts.forEach((post) => {
   196â†’    post.tags.forEach((tag) => {
   197â†’      const tagSlug = tag.toLowerCase();
   198â†’      tagMap.set(tagSlug, (tagMap.get(tagSlug) || 0) + 1);
   199â†’    });
   200â†’  });
   201â†’
   202â†’  const tags: Tag[] = Array.from(tagMap.entries()).map(([slug, count]) => ({
   203â†’    name: slug
   204â†’      .split('-')
   205â†’      .map((word) => word.charAt(0).toUpperCase() + word.slice(1))
   206â†’      .join(' '),
   207â†’    slug,
   208â†’    count,
   209â†’  }));
   210â†’
   211â†’  return tags.sort((a, b) => b.count - a.count);
   212â†’}
   213â†’
   214â†’/**
   215â†’ * Get related posts based on category and tags
   216â†’ * @param slug - The current post slug
   217â†’ * @param limit - Maximum number of related posts to return (default: 3)
   218â†’ * @returns Array of related blog posts
   219â†’ */
   220â†’export function getRelatedPosts(slug: string, limit = 3): BlogPost[] {
   221â†’  const currentPost = getPostBySlug(slug);
   222â†’  if (!currentPost) return [];
   223â†’
   224â†’  const allPosts = getAllPosts();
   225â†’
   226â†’  // Score posts based on shared category and tags
   227â†’  const scoredPosts = allPosts
   228â†’    .filter((post) => post.slug !== slug)
   229â†’    .map((post) => {
   230â†’      let score = 0;
   231â†’
   232â†’      // Same category: +3 points
   233â†’      if (post.category === currentPost.category) {
   234â†’        score += 3;
   235â†’      }
   236â†’
   237â†’      // Shared tags: +1 point per tag
   238â†’      const sharedTags = post.tags.filter((tag) =>
   239â†’        currentPost.tags.includes(tag)
   240â†’      );
   241â†’      score += sharedTags.length;
   242â†’
   243â†’      return { post, score };
   244â†’    })
   245â†’    .filter(({ score }) => score > 0)
   246â†’    .sort((a, b) => b.score - a.score);
   247â†’
   248â†’  return scoredPosts.slice(0, limit).map(({ post }) => post);
   249â†’}
   250â†’
   251â†’/**
   252â†’ * Get recent posts
   253â†’ * @param limit - Maximum number of posts to return (default: 5)
   254â†’ * @returns Array of recent blog posts
   255â†’ */
   256â†’export function getRecentPosts(limit = 5): BlogPost[] {
   257â†’  const posts = getAllPosts();
   258â†’  return posts.slice(0, limit);
   259â†’}
