     1â†’/**
     2â†’ * CMS Integration Layer
     3â†’ * File-based content management using MDX files stored in content/posts/
     4â†’ * Provides functions to read, parse, and query blog posts
     5â†’ */
     6â†’
     7â†’import fs from 'fs';
     8â†’import path from 'path';
     9â†’import matter from 'gray-matter';
    10â†’import readingTime from 'reading-time';
    11â†’import type {
    12â†’  BlogPost,
    13â†’  BlogPostMeta,
    14â†’  BlogPostFrontmatter,
    15â†’  Category,
    16â†’  Tag,
    17â†’} from './types';
    18â†’
    19â†’const CONTENT_DIR = path.join(process.cwd(), 'content', 'posts');
    20â†’
    21â†’/**
    22â†’ * Ensure content directory exists
    23â†’ */
    24â†’function ensureContentDirectory(): void {
    25â†’  if (!fs.existsSync(CONTENT_DIR)) {
    26â†’    fs.mkdirSync(CONTENT_DIR, { recursive: true });
    27â†’  }
    28â†’}
    29â†’
    30â†’/**
    31â†’ * Get all MDX/MD files from content directory
    32â†’ */
    33â†’function getPostFiles(): string[] {
    34â†’  ensureContentDirectory();
    35â†’
    36â†’  if (!fs.existsSync(CONTENT_DIR)) {
    37â†’    return [];
    38â†’  }
    39â†’
    40â†’  const files = fs.readdirSync(CONTENT_DIR);
    41â†’  return files.filter(
    42â†’    (file) => file.endsWith('.mdx') || file.endsWith('.md')
    43â†’  );
    44â†’}
    45â†’
    46â†’/**
    47â†’ * Parse a blog post file and return structured data
    48â†’ */
    49â†’function parsePostFile(filename: string): BlogPost | null {
    50â†’  try {
    51â†’    const slug = filename.replace(/\.(mdx|md)$/, '');
    52â†’    const filePath = path.join(CONTENT_DIR, filename);
    53â†’    const fileContents = fs.readFileSync(filePath, 'utf8');
    54â†’
    55â†’    const { data, content } = matter(fileContents);
    56â†’    const frontmatter = data as BlogPostFrontmatter;
    57â†’
    58â†’    // Calculate reading time
    59â†’    const stats = readingTime(content);
    60â†’
    61â†’    // Default values
    62â†’    const post: BlogPost = {
    63â†’      slug,
    64â†’      title: frontmatter.title || 'Untitled',
    65â†’      description: frontmatter.description || '',
    66â†’      date: frontmatter.date || new Date().toISOString(),
    67â†’      category: frontmatter.category || 'uncategorized',
    68â†’      tags: frontmatter.tags || [],
    69â†’      author: frontmatter.author || 'InfinityCards Team',
    70â†’      image: frontmatter.image,
    71â†’      content,
    72â†’      readingTime: Math.ceil(stats.minutes),
    73â†’      published: frontmatter.published !== false, // Default to true
    74â†’    };
    75â†’
    76â†’    return post;
    77â†’  } catch (error) {
    78â†’    console.error(`Error parsing post file ${filename}:`, error);
    79â†’    return null;
    80â†’  }
    81â†’}
    82â†’
    83â†’/**
    84â†’ * Get all blog posts
    85â†’ * @param includeUnpublished - Whether to include unpublished posts (default: false)
    86â†’ * @returns Array of blog posts sorted by date (newest first)
    87â†’ */
    88â†’export function getAllPosts(includeUnpublished = false): BlogPost[] {
    89â†’  const files = getPostFiles();
