    19â†’const CONTENT_DIR = path.join(process.cwd(), 'content', 'posts');
    20â†’
    21â†’/**
    22â†’ * Ensure content directory exists
    23â†’ */
    24â†’function ensureContentDirectory(): void {
    25â†’  if (!fs.existsSync(CONTENT_DIR)) {
    26â†’    fs.mkdirSync(CONTENT_DIR, { recursive: true });
    27â†’  }
    28â†’}
    29â†’
    30â†’/**
    31â†’ * Get all MDX/MD files from content directory
    32â†’ */
    33â†’function getPostFiles(): string[] {
    34â†’  ensureContentDirectory();
    35â†’
    36â†’  if (!fs.existsSync(CONTENT_DIR)) {
    37â†’    return [];
    38â†’  }
    39â†’
    40â†’  const files = fs.readdirSync(CONTENT_DIR);
    41â†’  return files.filter(
    42â†’    (file) => file.endsWith('.mdx') || file.endsWith('.md')
    43â†’  );
    44â†’}
    45â†’
    46â†’/**
