#!/bin/bash

# Auto-Build Environment Setup for AEO Implementation
# Generated by Planner Agent

set -e

echo "========================================"
echo "Starting AEO Development Environment"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# SETUP PYTHON ENVIRONMENT
# ============================================

echo ""
echo "Setting up Python environment..."

# Navigate to content-agents directory
cd claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents

# Check if virtual environment exists
if [ ! -d ".venv" ]; then
    echo "Creating Python virtual environment..."
    python3 -m venv .venv
fi

# Activate virtual environment
source .venv/bin/activate

# Install dependencies
if [ -f "requirements.txt" ]; then
    echo "Installing Python dependencies..."
    pip install -q -r requirements.txt
fi

# ============================================
# VERIFY ENVIRONMENT VARIABLES
# ============================================

echo ""
echo "Checking environment variables..."

if [ -z "$ANTHROPIC_API_KEY" ]; then
    echo -e "${YELLOW}WARNING: ANTHROPIC_API_KEY not set${NC}"
    echo "Please set your API key:"
    echo "  export ANTHROPIC_API_KEY='your-api-key-here'"
    echo ""
    echo "Or create a .env file in the content-agents directory"
fi

# ============================================
# START SERVICES
# ============================================

echo ""
echo "Starting FastAPI server..."

# Start the API server in the background
uvicorn api.main:app --reload --port 8000 &
API_PID=$!

# Wait for API to be ready
wait_for_service 8000 "FastAPI Server"

# ============================================
# SUMMARY
# ============================================

echo ""
echo "========================================"
echo "Environment Ready!"
echo "========================================"
echo ""
echo "Services:"
echo "  API Server: http://localhost:8000"
echo "  API Docs:   http://localhost:8000/api/docs"
echo ""
echo "Testing:"
echo "  Run tests:  pytest"
echo "  Run tests with coverage: pytest --cov"
echo ""
echo "To stop services:"
echo "  kill $API_PID"
echo ""
echo "Next Steps:"
echo "  1. Check API health: curl http://localhost:8000/"
echo "  2. View API docs: open http://localhost:8000/api/docs"
echo "  3. Run tests: pytest -v"
echo ""
