#!/bin/bash

# Auto-Build Environment Setup
# Generated by Planner Agent for AI Citation Monitoring & Optimization

set -e

echo "========================================"
echo "AI Citation Monitoring & Optimization"
echo "Starting Development Environment"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# ENVIRONMENT SETUP
# ============================================

echo ""
echo "Setting up Python virtual environment..."

# Check if we're in the content-agents directory
CONTENT_AGENTS_DIR="claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents"

if [ ! -d "$CONTENT_AGENTS_DIR" ]; then
    echo -e "${RED}Error: content-agents directory not found${NC}"
    exit 1
fi

cd "$CONTENT_AGENTS_DIR"

# Create virtual environment if it doesn't exist
if [ ! -d "venv" ]; then
    echo "Creating virtual environment..."
    python3 -m venv venv
fi

# Activate virtual environment
echo "Activating virtual environment..."
source venv/bin/activate

# Install dependencies
echo "Installing dependencies..."
pip install -q --upgrade pip
pip install -q -r requirements.txt

# Install APScheduler for citation scheduler
pip install -q apscheduler

# Initialize database
echo "Initializing database..."
python database/init_db.py

# ============================================
# START SERVICES
# ============================================

echo ""
echo "Starting FastAPI backend..."

# Start backend server
uvicorn api.main:app --reload --host 0.0.0.0 --port 8000 &
BACKEND_PID=$!

# Wait for backend to be ready
wait_for_service 8000 "FastAPI Backend"

# ============================================
# SUMMARY
# ============================================

echo ""
echo "========================================"
echo "Environment Ready!"
echo "========================================"
echo ""
echo "Services:"
echo "  Backend API:  http://localhost:8000"
echo "  API Docs:     http://localhost:8000/api/docs"
echo "  Health Check: http://localhost:8000/health"
echo ""
echo "Citation Monitoring Endpoints:"
echo "  POST /api/citation-monitoring/test-query"
echo "  GET  /api/citation-monitoring/citations"
echo "  GET  /api/citation-monitoring/recommendations"
echo "  GET  /api/citation-monitoring/alerts"
echo ""
echo "Press Ctrl+C to stop all services"
echo ""

# Wait for user interrupt
trap "echo '\nStopping services...'; kill $BACKEND_PID 2>/dev/null; exit 0" INT TERM

# Keep script running
wait $BACKEND_PID
