#!/bin/bash

# Auto-Build Environment Setup
# Generated by Planner Agent for SEO-Optimized Content Pipeline

set -e

echo "========================================"
echo "Starting Development Environment"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# ENVIRONMENT CHECK
# ============================================

echo ""
echo "Checking environment..."

# Check for .env file
if [ ! -f "claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents/.env.development" ]; then
    echo -e "${YELLOW}Warning: .env.development not found${NC}"
    echo "Creating from .env.example..."
    cp claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents/.env.example \
       claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents/.env.development
    echo -e "${YELLOW}Please configure .env.development with API keys${NC}"
fi

# Check for Python virtual environment
if [ ! -d "claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents/venv" ]; then
    echo -e "${YELLOW}Virtual environment not found, creating...${NC}"
    cd claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents
    python3 -m venv venv
    source venv/bin/activate
    pip install -r requirements.txt
    cd -
fi

# ============================================
# START SERVICES
# ============================================

echo ""
echo "Starting Content Agents API..."

cd claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents

# Activate virtual environment
source venv/bin/activate

# Set environment
export ENVIRONMENT=development

# Start API server
uvicorn api.main:app --reload --host 0.0.0.0 --port 8000 &
API_PID=$!

# Wait for API to be ready
wait_for_service 8000 "Content Agents API"

# ============================================
# SUMMARY
# ============================================

echo ""
echo "========================================"
echo "Environment Ready!"
echo "========================================"
echo ""
echo "Services:"
echo "  Content Agents API:  http://localhost:8000"
echo "  API Documentation:   http://localhost:8000/api/docs"
echo ""
echo "Process IDs:"
echo "  API: $API_PID"
echo ""
echo "To stop services:"
echo "  kill $API_PID"
echo ""
echo "To view logs:"
echo "  tail -f claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents/logs/*.log"
echo ""
