#!/bin/bash

# Auto-Build Environment Setup for Email Platform Integration (Klaviyo)
# Generated by Planner Agent

set -e

echo "========================================"
echo "Starting Development Environment"
echo "Task: Email Platform Integration (Klaviyo)"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# ENVIRONMENT SETUP
# ============================================

echo ""
echo "Setting up Python environment..."

# Navigate to content-agents directory
CONTENT_AGENTS_DIR="./claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents"

if [ ! -d "$CONTENT_AGENTS_DIR" ]; then
    echo -e "${RED}Error: content-agents directory not found${NC}"
    exit 1
fi

cd "$CONTENT_AGENTS_DIR"

# Check if virtual environment exists
if [ ! -d "venv" ]; then
    echo "Creating Python virtual environment..."
    python3 -m venv venv
fi

# Activate virtual environment
echo "Activating virtual environment..."
source venv/bin/activate

# Install/update dependencies
echo "Installing dependencies..."
pip install -q --upgrade pip
pip install -q -r requirements.txt

# ============================================
# DATABASE SETUP
# ============================================

echo ""
echo "Setting up database..."

# Check if database initialization is needed
if [ ! -f "content_agents.db" ]; then
    echo "Initializing database..."
    python database/init_db.py
else
    echo "Database already exists"
fi

# ============================================
# CONFIGURATION CHECK
# ============================================

echo ""
echo "Checking configuration..."

# Check for environment file
if [ ! -f ".env.development" ] && [ ! -f ".env" ]; then
    echo -e "${YELLOW}Warning: No .env file found. Copy .env.example to .env and configure.${NC}"
fi

# Verify required configuration
python -c "
from config.config import ANTHROPIC_API_KEY
if not ANTHROPIC_API_KEY:
    print('${YELLOW}Warning: ANTHROPIC_API_KEY not set${NC}')
" 2>/dev/null || echo -e "${YELLOW}Warning: Could not verify configuration${NC}"

# ============================================
# START SERVICES
# ============================================

echo ""
echo "Starting FastAPI server..."
echo ""

# Start FastAPI server in development mode
uvicorn api.main:app --host 0.0.0.0 --port 8000 --reload &

# Wait for FastAPI to be ready
wait_for_service 8000 "FastAPI Server"

# ============================================
# SUMMARY
# ============================================

echo ""
echo "========================================"
echo "Environment Ready!"
echo "========================================"
echo ""
echo "Services:"
echo "  API Server:  http://localhost:8000"
echo "  API Docs:    http://localhost:8000/api/docs"
echo "  Health:      http://localhost:8000/health"
echo ""
echo "Klaviyo Integration Endpoints:"
echo "  POST /api/klaviyo/sync/profiles - Sync customer profiles"
echo "  POST /api/klaviyo/events - Track events"
echo "  GET  /api/klaviyo/lists - Get email lists"
echo ""
echo "Run tests:"
echo "  pytest tests/test_klaviyo_client.py -v"
echo "  pytest tests/test_api_klaviyo.py -v"
echo ""
echo "Press Ctrl+C to stop all services"
echo "========================================"

# Wait for interrupt
wait
