#!/bin/bash

# Auto-Build Environment Setup - Welcome & Nurture Email Sequences
# Generated by Planner Agent

set -e

echo "========================================"
echo "Starting Development Environment"
echo "Welcome & Nurture Email Sequences"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# INSTALL DEPENDENCIES
# ============================================

echo ""
echo "Installing dependencies..."

# Email Automation Service
echo -e "${YELLOW}Email Automation Service${NC}"
cd claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/04-email-marketing-automation/implementation
if [ ! -d "node_modules" ]; then
    npm install
fi
cd - > /dev/null

# Dashboard
echo -e "${YELLOW}Dashboard${NC}"
cd claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/dashboard
if [ ! -d "node_modules" ]; then
    npm install
fi
cd - > /dev/null

# ============================================
# START SERVICES
# ============================================

echo ""
echo "Starting services..."

# Start Email Automation Service (in background)
echo -e "${YELLOW}Starting Email Automation Service...${NC}"
cd claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/04-email-marketing-automation/implementation
npm run dev > /tmp/email-automation.log 2>&1 &
EMAIL_PID=$!
cd - > /dev/null

# Wait for email automation to be ready
wait_for_service 3001 "Email Automation Service"

# Start Dashboard (in background)
echo -e "${YELLOW}Starting Dashboard...${NC}"
cd claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/dashboard
npm run dev > /tmp/dashboard.log 2>&1 &
DASHBOARD_PID=$!
cd - > /dev/null

# Wait for dashboard to be ready
wait_for_service 3000 "Dashboard"

# ============================================
# SUMMARY
# ============================================

echo ""
echo "========================================"
echo "Environment Ready!"
echo "========================================"
echo ""
echo "Services:"
echo "  Dashboard:        http://localhost:3000"
echo "  Email Automation: http://localhost:3001"
echo ""
echo "Logs:"
echo "  Dashboard:        tail -f /tmp/dashboard.log"
echo "  Email Automation: tail -f /tmp/email-automation.log"
echo ""
echo "PIDs:"
echo "  Dashboard:        $DASHBOARD_PID"
echo "  Email Automation: $EMAIL_PID"
echo ""
echo "To stop services:"
echo "  kill $DASHBOARD_PID $EMAIL_PID"
echo ""
echo "========================================"
