#!/bin/bash

# Abandoned Cart Recovery - Environment Setup
# Generated by Planner Agent

set -e

echo "========================================"
echo "Abandoned Cart Recovery - Dev Environment"
echo "========================================"

# Colors
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
NC='\033[0m'

# Wait for service function
wait_for_service() {
    local port=$1
    local name=$2
    local max=30
    local count=0

    echo "Waiting for $name on port $port..."
    while ! nc -z localhost $port 2>/dev/null; do
        count=$((count + 1))
        if [ $count -ge $max ]; then
            echo -e "${RED}$name failed to start${NC}"
            return 1
        fi
        sleep 1
    done
    echo -e "${GREEN}$name ready${NC}"
}

# ============================================
# CHECK PREREQUISITES
# ============================================

echo ""
echo "Checking prerequisites..."

# Check Python
if ! command -v python3 &> /dev/null; then
    echo -e "${RED}Python 3 not found${NC}"
    exit 1
fi
echo -e "${GREEN}✓ Python 3 found${NC}"

# Check Redis
if ! command -v redis-cli &> /dev/null; then
    echo -e "${YELLOW}⚠ Redis CLI not found - Celery tasks may not work${NC}"
else
    echo -e "${GREEN}✓ Redis found${NC}"
fi

# ============================================
# INSTALL DEPENDENCIES
# ============================================

echo ""
echo "Installing dependencies..."

cd claude-code-os-implementation/03-ai-growth-engine/organic-marketing-package/content-agents

if [ ! -d ".venv" ]; then
    echo "Creating virtual environment..."
    python3 -m venv .venv
fi

source .venv/bin/activate

echo "Installing Python packages..."
pip install -q --upgrade pip
pip install -q -r requirements.txt

echo -e "${GREEN}✓ Dependencies installed${NC}"

# ============================================
# SETUP DATABASE
# ============================================

echo ""
echo "Setting up database..."

if [ -f "database/init_db.py" ]; then
    python database/init_db.py
    echo -e "${GREEN}✓ Database initialized${NC}"
else
    echo -e "${YELLOW}⚠ Database init script not found - skipping${NC}"
fi

# ============================================
# CHECK ENVIRONMENT VARIABLES
# ============================================

echo ""
echo "Checking environment variables..."

if [ -z "$SENDGRID_API_KEY" ]; then
    echo -e "${YELLOW}⚠ SENDGRID_API_KEY not set - email service will not work${NC}"
    echo "  Set with: export SENDGRID_API_KEY=your_key_here"
else
    echo -e "${GREEN}✓ SENDGRID_API_KEY set${NC}"
fi

if [ -z "$REDIS_URL" ]; then
    echo -e "${YELLOW}⚠ REDIS_URL not set - using default: redis://localhost:6379${NC}"
    export REDIS_URL="redis://localhost:6379"
else
    echo -e "${GREEN}✓ REDIS_URL set${NC}"
fi

# ============================================
# START SERVICES
# ============================================

echo ""
echo "Starting services..."

# Start Redis (if not running)
if ! redis-cli ping &> /dev/null; then
    echo "Starting Redis..."
    redis-server --daemonize yes
    sleep 2
    if redis-cli ping &> /dev/null; then
        echo -e "${GREEN}✓ Redis started${NC}"
    else
        echo -e "${RED}✗ Redis failed to start${NC}"
    fi
else
    echo -e "${GREEN}✓ Redis already running${NC}"
fi

# Start FastAPI backend
echo ""
echo "Starting FastAPI backend..."
uvicorn api.main:app --reload --host 0.0.0.0 --port 8000 > /tmp/fastapi.log 2>&1 &
BACKEND_PID=$!
wait_for_service 8000 "FastAPI Backend"

# Start Celery worker (if celery_app.py exists)
if [ -f "celery_app.py" ]; then
    echo ""
    echo "Starting Celery worker..."
    celery -A celery_app worker --loglevel=info > /tmp/celery_worker.log 2>&1 &
    CELERY_WORKER_PID=$!
    sleep 3
    echo -e "${GREEN}✓ Celery worker started (PID: $CELERY_WORKER_PID)${NC}"

    echo ""
    echo "Starting Celery beat scheduler..."
    celery -A celery_app beat --loglevel=info > /tmp/celery_beat.log 2>&1 &
    CELERY_BEAT_PID=$!
    sleep 2
    echo -e "${GREEN}✓ Celery beat started (PID: $CELERY_BEAT_PID)${NC}"
else
    echo -e "${YELLOW}⚠ celery_app.py not found - skipping Celery services${NC}"
fi

# ============================================
# SUMMARY
# ============================================

echo ""
echo "========================================"
echo "Environment Ready!"
echo "========================================"
echo ""
echo "Services:"
echo "  Backend API:    http://localhost:8000"
echo "  API Docs:       http://localhost:8000/api/docs"
echo "  Health Check:   http://localhost:8000/health"
echo ""
echo "Logs:"
echo "  FastAPI:        tail -f /tmp/fastapi.log"
echo "  Celery Worker:  tail -f /tmp/celery_worker.log"
echo "  Celery Beat:    tail -f /tmp/celery_beat.log"
echo ""
echo "PIDs:"
echo "  Backend:        $BACKEND_PID"
if [ -n "$CELERY_WORKER_PID" ]; then
    echo "  Celery Worker:  $CELERY_WORKER_PID"
    echo "  Celery Beat:    $CELERY_BEAT_PID"
fi
echo ""
echo "To stop services:"
echo "  kill $BACKEND_PID"
if [ -n "$CELERY_WORKER_PID" ]; then
    echo "  kill $CELERY_WORKER_PID $CELERY_BEAT_PID"
fi
echo ""
echo "Next steps:"
echo "  1. Implement subtask-1-1: Create database models"
echo "  2. Run tests: pytest tests/test_cart_recovery.py"
echo "  3. Check API: curl http://localhost:8000/api/cart/track"
echo ""
